// ToDoItem.js - Component to render each to-do item
import React from 'react';
import './ToDoItem.css';
const ToDoItem = ({ text, completed, toggleToDo, deleteToDo }) => {
  return (
    <div className="todo-item">
      <input type="checkbox" checked={completed} onChange={toggleToDo} />
      <span className={`todo-text ${completed ? 'completed' : ''}`}>{text}</span>
      <button onClick={deleteToDo} className="delete-button">
        Delete
      </button>
    </div>
  );
};

export default ToDoItem;
