// App.js - Main container for the application
import React, { useState } from 'react';
import ToDoList from './ToDoList';
import './App.css'; // You need to create this CSS file to style your components

const App = () => {
  const [toDos, setToDos] = useState([]);

  // Function to add a new to-do item
  const addToDo = (text) => {
    const newToDos = [...toDos, { text, completed: false }];
    setToDos(newToDos);
  };

  // Function to toggle the completed state of a to-do item
  const toggleToDo = (index) => {
    const newToDos = toDos.map((item, i) => {
      if (i === index) {
        return { ...item, completed: !item.completed };
      }
      return item;
    });
    setToDos(newToDos);
  };

  // Function to delete a to-do item
  const deleteToDo = (index) => {
    const newToDos = toDos.filter((_, i) => i !== index);
    setToDos(newToDos);
  };

  return (
    <div className="app">
      <ToDoList
        items={toDos}
        toggleToDo={toggleToDo}
        deleteToDo={deleteToDo}
      />
      <button className="add-button" onClick={() => addToDo(prompt('Enter to-do item'))}>
        Add To-Do
      </button>
    </div>
  );
};

export default App;
