// App.js - Main container for the application
import React, { useState } from 'react';
import ToDoList from './ToDoList';
import Snackbar from './Snackbar';
import './ToDoItem.css';
import './App.css';

const App = () => {
  const [toDos, setToDos] = useState([]);
  const [newToDoText, setNewToDoText] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [showSnackbar, setShowSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');

  const addToDo = () => {
    if (newToDoText.trim() !== '') {
      const newToDo = {
        id: Date.now(),
        text: newToDoText,
        completed: false,
        dueDate: dueDate,
      };

      const newToDos = [...toDos, newToDo];
      setToDos(newToDos);
      setNewToDoText('');
      setDueDate('');
      showSnackbarMessage('Item added successfully');
    } else {
      showSnackbarMessage('Please enter a valid ToDo item');
    }
  };

  const toggleToDo = (id) => {
    const newToDos = toDos.map((item) => {
      if (item.id === id) {
        return { ...item, completed: !item.completed };
      }
      return item;
    });
    setToDos(newToDos);
    showSnackbarMessage('Item updated successfully');
  };

  const deleteToDo = (id) => {
    const newToDos = toDos.filter((item) => item.id !== id);
    setToDos(newToDos);
    showSnackbarMessage('Item deleted successfully');
  };

  const showSnackbarMessage = (message) => {
    setSnackbarMessage(message);
    setShowSnackbar(true);
  };

  const closeSnackbar = () => {
    setShowSnackbar(false);
    setSnackbarMessage('');
  };

  return (
    <div className="app">
      <div className="add-todo-container">
        <input
          className="input-field"
          type="text"
          placeholder="Enter to-do item"
          value={newToDoText}
          onChange={(e) => setNewToDoText(e.target.value)}
        />
        <input
          className="due-date-field"
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <button className="add-button" onClick={addToDo}>
          <i className="fas fa-calendar"></i> Add To-Do
        </button>
      </div>

      <ToDoList items={toDos} toggleToDo={toggleToDo} deleteToDo={deleteToDo} />
      {showSnackbar && <Snackbar message={snackbarMessage} onClose={closeSnackbar} />}
    </div>
  );
};

export default App;
