// App.js - Main container for the application
import React, { useState } from 'react';
import ToDoList from './ToDoList';
import './App.css'; // You need to create this CSS file to style your components

const App = () => {
  const [toDos, setToDos] = useState([]);
  const [newToDoText, setNewToDoText] = useState(''); // New state to store the text of the new to-do

  // Function to add a new to-do item
  const addToDo = () => {
    // Check if the text is not empty before adding
    if (newToDoText.trim() !== '') {
      const newToDos = [...toDos, { text: newToDoText, completed: false }];
      setToDos(newToDos);
      setNewToDoText(''); // Clear the input field after adding a to-do
    }
  };

  // Function to toggle the completed state of a to-do item
  const toggleToDo = (index) => {
    const newToDos = toDos.map((item, i) => {
      if (i === index) {
        return { ...item, completed: !item.completed };
      }
      return item;
    });
    setToDos(newToDos);
  };

  // Function to delete a to-do item
  const deleteToDo = (index) => {
    const newToDos = toDos.filter((_, i) => i !== index);
    setToDos(newToDos);
  };

  return (
    <div className="app">
      <ToDoList items={toDos} toggleToDo={toggleToDo} deleteToDo={deleteToDo} />

      <div className="add-todo-container">
        <input
          className="input-field"
          type="text"
          placeholder="Enter to-do item"
          value={newToDoText}
          onChange={(e) => setNewToDoText(e.target.value)}
        />
        <button className="add-button" onClick={addToDo}>
          Add To-Do
        </button>
      </div>
    </div>
  );
};

export default App;
